pragma solidity ^0.8.0;

interface IUniswapV2Pair {
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function mint(address to) external returns (uint liquidity);
    function burn(address to) external returns (uint amount0, uint amount1);
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
}

contracts/smartVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract SmartVault is Ownable {
    IERC20 public immutable stableToken; // WGBD
    address public seedNode;

    uint256 public totalDeposits;
    uint256 public totalProfits;
    uint256 public currentDistributionId;

    uint256 public investmentReserve;
    uint256 public productReserve;
    uint256 public redemptionReserve;

    struct UserInfo {
        uint128 amount;
        uint64 depositTime;
        uint256 claimedDistribution;
        uint256 recommitCount;
        uint32 unlockQuarter;
        uint32 committedQuarters;
        uint32 unlockQuarter;
    }

    struct Redemption {
        address user;
        uint128 amount;
        uint256 requestQuarter;
        bool fulfilled;
    }

    Redemption[] public redemptionQueue;
    mapping(address => uint256[]) public userRedemptionIds;
    mapping(address => UserInfo) public users;
    mapping(address => bool) public hasLockedRedemption;
    mapping(uint256 => uint256) public distributionProfits;

    event Deposited(address indexed user, uint128 amount, uint32 committedQuarters);
    event Recommitted(address indexed user, uint256 newQuartersCommitted);
    event ProfitAdded(address indexed from, uint128 amount);
    event Distributed(uint256 id, uint256 totalProfit);
    event Claimed(address indexed user, uint256 reward);
    event Withdrawn(address indexed user, uint128 amount);
    event CapitalAllocated(uint256 investment, uint256 products, uint256 redemptions);
    event CapitalSpent(address indexed target, uint128 amount, string purpose);

    modifier onlySeed() {
        require(msg.sender == seedNode, "Not authorized");
        _;
    }

    constructor(address initialOwner, address _stableToken, address _seedNode) Ownable(initialOwner) {
        require(_stableToken != address(0), "Invalid stable token");
        require(_seedNode != address(0), "Invalid seed node");
        stableToken = IERC20(_stableToken);
        seedNode = _seedNode;
    }

    function deposit(uint128 amount, uint32 committedQuarters) external {
        require(amount > 0, "Zero amount");
        require(committedQuarters >= 1, "Minimum 1 quarter commitment");

        stableToken.transferFrom(msg.sender, address(this), amount);

        UserInfo storage user = users[msg.sender];

        if (user.amount == 0) {
            user.depositTime = block.timestamp;
            user.committedQuarters = committedQuarters;
            user.eligibleQuarter = computeEligibilityQuarter(block.timestamp, committedQuarters);
            user.unlockQuarter = getQuarter(block.timestamp) + committedQuarters;
        }

        user.amount += amount;
        totalDeposits += amount;
        hasLockedRedemption[msg.sender] = true;

        emit Deposited(msg.sender, amount, committedQuarters);
    }

    function recommit(uint32 additionalQuarters) external {
        UserInfo storage user = users[msg.sender];
        require(block.timestamp >= user.depositTime + (user.committedQuarters * 90 days), "Still locked");

        user.depositTime = block.timestamp;
        user.recommitCount += 1;
        user.committedQuarters = additionalQuarters;
        user.eligibleQuarter = computeEligibilityQuarter(block.timestamp, additionalQuarters);
        user.unlockQuarter = getQuarter(block.timestamp) + additionalQuarters;

        emit Recommitted(msg.sender, additionalQuarters);
    }

    function requestRedemption() external {
        UserInfo storage user = users[msg.sender];
        require(user.amount > 0, "Nothing to redeem");
        require(getQuarter(block.timestamp) >= user.unlockQuarter, "Still locked");

        uint128 amount = user.amount;
        user.amount = 0;
        totalDeposits -= amount;

        redemptionQueue.push(Redemption({
            user: msg.sender,
            amount: amount,
            requestQuarter: getQuarter(block.timestamp),
            fulfilled: false
        }));

        userRedemptionIds[msg.sender].push(redemptionQueue.length - 1);
        hasLockedRedemption[msg.sender] = false;
    }

    function getOutstandingRedemptions() external view returns (Redemption[] memory) {
        uint256 count;
        for (uint256 i = 0; i < redemptionQueue.length; i++) {
            if (!redemptionQueue[i].fulfilled) count++;
        }

        Redemption[] memory pending = new Redemption[](count);
        uint256 j;
        for (uint256 i = 0; i < redemptionQueue.length; i++) {
            if (!redemptionQueue[i].fulfilled) {
                pending[j++] = redemptionQueue[i];
            }
        }

        return pending;
    }



    function addProfit(uint128 amount) external {
        require(amount > 0, "Zero profit");
        stableToken.transferFrom(msg.sender, address(this), amount);
        totalProfits += amount;

        emit ProfitAdded(msg.sender, amount);
    }

    function processRedemptions() public onlySeed {
        for (uint256 i = 0; i < redemptionQueue.length; i++) {
            Redemption storage r = redemptionQueue[i];
            if (!r.fulfilled && stableToken.balanceOf(address(this)) >= r.amount) {
                r.fulfilled = true;
                redemptionReserve -= r.amount;
                stableToken.transfer(r.user, r.amount);
                emit Withdrawn(r.user, r.amount);
            }
        }
    }


    function distribute() external onlySeed {
        require(totalProfits > 0, "No profits available");
        distributionProfits[++currentDistributionId] = totalProfits;
        emit Distributed(currentDistributionId, totalProfits);
        totalProfits = 0;
    }

    function distributeTo(address userAddr) public onlySeed {
        UserInfo storage user = users[userAddr];
        if (user.amount == 0) return;
        if (getQuarter(block.timestamp) < user.eligibleQuarter) return;
        if (currentDistributionId <= user.claimedDistribution) return;

        uint256 reward;
        for (uint256 i = user.claimedDistribution + 1; i <= currentDistributionId; i++) {
            uint256 profit = distributionProfits[i];
            uint256 multiplier = getMultiplier(user.depositTime, user.committedQuarters);
            reward += (user.amount * multiplier * profit) / (totalDeposits * 100);
        }

        user.claimedDistribution = currentDistributionId;
        if (reward > 0) {
            stableToken.transfer(userAddr, reward);
            emit Claimed(userAddr, reward);
        }
    }

    function batchDistribute(address[] calldata userList) external onlySeed {
        for (uint256 i = 0; i < userList.length; i++) {
            distributeTo(userList[i]);
        }
    }

    function allocateCapital(uint256 forInvestment, uint256 forProducts, uint256 forRedemptions) external onlyOwner {
        uint256 total = forInvestment + forProducts + forRedemptions;
        require(total <= stableToken.balanceOf(address(this)), "Overallocated");

        investmentReserve = forInvestment;
        productReserve = forProducts;
        redemptionReserve = forRedemptions;

        emit CapitalAllocated(forInvestment, forProducts, forRedemptions);
    }

    function spendCapital(address to, uint128 amount, string calldata purpose) external onlyOwner {
        require(to != address(0), "Invalid target");
        require(amount <= investmentReserve, "Insufficient capital");

        investmentReserve -= amount;
        stableToken.transfer(to, amount);

        emit CapitalSpent(to, amount, purpose);
    }

    // --- Helpers ---

    function getMultiplier(uint64 depositTime, uint32 committedQuarters) public view returns (uint256) {
        uint256 depositQ = getQuarter(depositTime);
        uint256 nowQ = getQuarter(block.timestamp);
        uint256 held = nowQ > depositQ ? nowQ - depositQ : 0;

        // Base multipliers based on committed quarters
        uint256 baseMultiplier;
        if (committedQuarters >= 6) {
            baseMultiplier = 200;
        } else if (committedQuarters >= 4) {
            baseMultiplier = 150;

        } else {
            baseMultiplier = 125;
        }

        // Apply full multiplier only if user actually held their deposit at least the committed duration
        if (held >= committedQuarters) {
            return baseMultiplier;
        }

        // Otherwise, scale proportionally to how long they actually held it (minimum 100%)
        uint256 scaled = 100 + ((baseMultiplier - 100) * held) / committedQuarters;
        return scaled > baseMultiplier ? baseMultiplier : scaled;
    }


    function computeEligibilityQuarter(uint64 timestamp, uint32 committedQuarters) public pure returns (uint256) {
        uint32 quarter = getQuarter(timestamp);
        uint32 startOfQ = getQuarterStart(timestamp);
        uint32 daysIntoQuarter = (timestamp - startOfQ) / 1 days;

        uint32 graceWindow = committedQuarters >= 3 ? 20 : 0;

        return daysIntoQuarter <= graceWindow ? quarter : quarter + 1;
    }


    function getQuarter(uint256 ts) public pure returns (uint256) {
        uint32 year = 1970 + (ts / 31556926);
        uint32 month = (ts / 2592000) % 12 + 1;
        uint32 quarter = (month - 1) / 3 + 1;
        return year * 4 + quarter;
    }

    function getQuarterStart(uint256 ts) public pure returns (uint256) {
        uint32 year = 1970 + (ts / 31556926);
        uint32 month = (ts / 2592000) % 12 + 1;
        uint32 quarterStartMonth = ((month - 1) / 3) * 3 + 1;
        return toTimestamp(year, quarterStartMonth, 1);
    }

    function toTimestamp(uint32 year, uint32 month, uint32 day) internal pure returns (uint256) {
        return (year - 1970) * 365 days + (month - 1) * 30 days + (day - 1) * 1 days;
    }

    function getUserVaultInfo(address user) external view returns (
        uint128 amount,
        uint32 committedQuarters,
        uint32 unlockQuarter,
        uint256[] memory redemptionIds,
        Redemption[] memory redemptions
    ) {
        UserInfo memory u = users[user];
        amount = u.amount;
        committedQuarters = u.committedQuarters;
        unlockQuarter = u.unlockQuarter;

        // Get the list of redemption IDs associated with the user
        redemptionIds = userRedemptionIds[user];

        // Prepare array for redemptions
        redemptions = new Redemption[](redemptionIds.length);
        for (uint256 i = 0; i < redemptionIds.length; i++) {
            redemptions[i] = redemptionQueue[redemptionIds[i]];
        }

        return (amount, committedQuarters, unlockQuarter, redemptionIds, redemptions);
    }

    function getUserDepositStatus(address user) external view returns (
        uint256 depositAmount,
        uint256 depositStartTime,
        uint256 elapsedDays,
        uint256 remainingDays,
        bool isPending,
        bool isClosed
    ) {
        UserInfo memory u = users[user];

        depositAmount = u.amount;
        depositStartTime = u.depositTime;

        // If user has no deposit
        if (depositAmount == 0) {
            return (0, 0, 0, 0, false, true);
        }

        uint256 lockPeriodSeconds = u.committedQuarters * 90 days;
        uint256 depositEndTime = u.depositTime + lockPeriodSeconds;

        if (block.timestamp < depositEndTime) {
            // Still within lock period
            elapsedDays = (block.timestamp - u.depositTime) / 1 days;
            remainingDays = (depositEndTime - block.timestamp) / 1 days;
            isPending = true;
            isClosed = false;
        } else {
            // Lock period completed
            elapsedDays = u.committedQuarters * 90;
            remainingDays = 0;
            isPending = false;
            isClosed = true;
        }
    }

}

contracts/stableSwapGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract StableSwapGateway is Ownable {
    using SafeERC20 for IERC20;

    IERC20 public immutable GBD;
    uint256 public feeBasisPoints = 125; // 1.25%
    uint256 public constant MAX_BPS = 10000;

    enum SwapStatus { Pending, Completed, Refunded }

    struct SwapRecord {
        address fromToken;
        address toToken;
        uint256 fromAmount;
        uint256 toAmount;
        uint256 fee;
        SwapStatus status;
        uint64 timestamp;
    }

    mapping(address => SwapRecord[]) private userSwaps;
    mapping(address => bool) public stablecoinWhitelist;
    mapping(address => bool) public redemptionLocked;
    mapping(address => bool) public authorizedLockers;

    event StableSwapped(address indexed user, address indexed stable, uint256 stableIn, uint256 gbdOut, uint256 fee);
    event StableRefunded(address indexed user, address indexed stable, uint256 gbdIn, uint256 stableOut, uint256 fee);
    event FeeUpdated(uint256 newFeeBps);
    event StablecoinToggled(address stable, bool enabled);

    constructor(address initialOwner, address _gbd, address[] memory initialStables) Ownable(initialOwner) {
        require(_gbd != address(0), "Invalid GBD address");
        GBD = IERC20(_gbd);

        for (uint256 i = 0; i < initialStables.length; i++) {
            address token = initialStables[i];
            require(token != address(0), "Zero address not allowed");
            stablecoinWhitelist[token] = true;
            emit StablecoinToggled(token, true);
        }
    }

    function toggleStablecoin(address stable, bool isEnabled) external onlyOwner {
        require(stable != address(0), "Invalid stablecoin address");
        stablecoinWhitelist[stable] = isEnabled;
        emit StablecoinToggled(stable, isEnabled);
    }

    function setFee(uint256 newFeeBps) external onlyOwner {
        require(newFeeBps <= 500, "Fee too high");
        feeBasisPoints = newFeeBps;
        emit FeeUpdated(newFeeBps);
    }

    function setAuthorizedLocker(address locker, bool status) external onlyOwner {
        require(locker != address(0), "Invalid locker");
        authorizedLockers[locker] = status;
    }

    // Swap stablecoin for GBD
    function swapStableForGBD(address stable, uint128 amount) external {
        require(stablecoinWhitelist[stable], "Stablecoin not supported");
        require(amount > 0, "Amount must be > 0");

        IERC20 stableToken = IERC20(stable);
        stableToken.safeTransferFrom(msg.sender, address(this), amount);

        uint256 fee = (amount * feeBasisPoints) / MAX_BPS;
        uint256 gbdOut = amount - fee;

        require(GBD.balanceOf(address(this)) >= gbdOut, "Insufficient GBD liquidity");
        GBD.safeTransfer(msg.sender, gbdOut);

        emit StableSwapped(msg.sender, stable, amount, gbdOut, fee);

        userSwaps[msg.sender].push(SwapRecord({
            fromToken: stable,
            toToken: address(GBD),
            fromAmount: amount,
            toAmount: gbdOut,
            fee: fee,
            status: SwapStatus.Completed,
            timestamp: block.timestamp
        }));
    }

    // Swap GBD for stablecoin
    function swapGBDForStable(address stable, uint256 gbdAmount) external {
        require(stablecoinWhitelist[stable], "Stablecoin not supported");
        require(gbdAmount > 0, "Amount must be > 0");

        uint256 fee = (gbdAmount * feeBasisPoints) / MAX_BPS;
        uint256 stableOut = gbdAmount - fee;

        IERC20 stableToken = IERC20(stable);
        require(stableToken.balanceOf(address(this)) >= stableOut, "Insufficient stablecoin liquidity");

        GBD.safeTransferFrom(msg.sender, address(this), gbdAmount);
        stableToken.safeTransfer(msg.sender, stableOut);

        emit StableRefunded(msg.sender, stable, gbdAmount, stableOut, fee);

        userSwaps[msg.sender].push(SwapRecord({
            fromToken: address(GBD),
            toToken: stable,
            fromAmount: gbdAmount,
            toAmount: stableOut,
            fee: fee,
            status: SwapStatus.Completed,
            timestamp: block.timestamp
        }));
    }

    // Unlock redemption (by authorized lockers)
    function unlockRedemption(address user) external {
        require(authorizedLockers[msg.sender], "Not authorized");
        require(user != address(0), "Invalid user");
        redemptionLocked[user] = false;
        // Optionally emit event
    }

    // Return GBD for stable (by owner)
    function returnGBDForStable(address user, address stable, uint256 gbdAmount) external onlyOwner {
        require(user != address(0), "Invalid user");
        require(stablecoinWhitelist[stable], "Stablecoin not supported");
        require(gbdAmount > 0, "Zero GBD");

        uint256 fee = 0;
        uint256 stableOut = gbdAmount - fee;

        IERC20 stableToken = IERC20(stable);
        require(stableToken.balanceOf(address(this)) >= stableOut, "Not enough stablecoin liquidity");

        GBD.safeTransferFrom(user, address(this), gbdAmount);
        stableToken.safeTransfer(user, stableOut);

        emit StableRefunded(user, stable, gbdAmount, stableOut, fee);

        userSwaps[msg.sender].push(SwapRecord({
            fromToken: address(GBD),
            toToken: stable,
            fromAmount: gbdAmount,
            toAmount: stableOut,
            fee: fee,
            status: SwapStatus.Completed,
            timestamp: block.timestamp
        }));
    }

    // Get user swaps
    function getUserSwaps(address user) external view returns (SwapRecord[] memory) {
        SwapRecord[] storage swaps = userSwaps[user];
        SwapRecord[] memory swapsCopy = new SwapRecord[](swaps.length);
        for (uint256 i = 0; i < swaps.length; i++) {
            swapsCopy[i] = swaps[i];
        }
        return swapsCopy;
    }

    function getUserSwapsByStatus(address user, SwapStatus filterStatus) external view returns (SwapRecord[] memory) {
        SwapRecord[] storage swaps = userSwaps[user];
        uint256 count = 0;

        // First pass: count how many swaps match the filter
        for (uint256 i = 0; i < swaps.length; i++) {
            if (swaps[i].status == filterStatus) {
                count++;
            }
        }

        // Create a new array with the size of matched swaps
        SwapRecord[] memory filteredSwaps = new SwapRecord[](count);
        uint256 index = 0;

        // Second pass: populate the filtered array
        for (uint256 i = 0; i < swaps.length; i++) {
            if (swaps[i].status == filterStatus) {
                filteredSwaps[index] = swaps[i];
                index++;
            }
        }

        return filteredSwaps;
    }
}

contracts/transferTracker.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract TransferTracker {
    enum Status { Pending, Completed, Failed }

    struct Transfer {
        address sender;
        address recipient;
        address token;
        uint128 amount;
        Status status;
        bytes32 txhash;
        uint64 timestamp;
    }

    // Store all transfers globally
    Transfer[] public allTransfers;

    // Mapping from user address to their transfer indices
    mapping(address => uint256[]) private userTransferIndices;

    event TransferRecorded(
        address indexed user,
        uint256 indexed transferId,
        address indexed sender,
        address recipient,
        address token,
        uint128 amount,
        Status status,
        bytes32 txhash,
        uint64 timestamp
    );

    function recordTransfer(
        address _recipient,
        address _token,
        uint256 _amount,
        Status _status,
        bytes32 _txhash
    ) external {
        Transfer memory newTransfer = Transfer({
            sender: msg.sender,
            recipient: _recipient,
            token: _token,
            amount: _amount,
            status: _status,
            txhash: _txhash,
            timestamp: block.timestamp
        });

        allTransfers.push(newTransfer);
        uint256 index = allTransfers.length - 1;
        // Save index for involved users
        userTransferIndices[msg.sender].push(index);
        userTransferIndices[_recipient].push(index);

        emit TransferRecorded(
            msg.sender,
            index,
            msg.sender,
            _recipient,
            _token,
            _amount,
            _status,
            _txhash,
            block.timestamp
        );
    }

    function getAllTransfersCount() external view returns (uint256) {
        return allTransfers.length;
    }

    function getTransfer(uint256 index) external view returns (
        address sender,
        address recipient,
        address token,
        uint128 amount,
        Status status,
        bytes32 txhash,
        uint64 timestamp
    ) {
        require(index < allTransfers.length, "Index out of bounds");
        Transfer memory t = allTransfers[index];
        return (t.sender, t.recipient, t.token, t.amount, t.status, t.txhash, t.timestamp);
    }

    function getTransfersInvolvingUser(address user) external view returns (Transfer[] memory) {
        uint256 total = userTransferIndices[user].length;
        Transfer[] memory involvedTransfers = new Transfer[](total);
        for (uint256 i = 0; i < total; i++) {
            involvedTransfers[i] = allTransfers[userTransferIndices[user][i]];
        }
        return involvedTransfers;
    }
}

