contracts/userQueryHub.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface ITransferTracker {
    struct Transfer {
        address sender;
        address recipient;
        address token;
        uint128 amount;
        uint8 status;
        bytes32 txhash;
        uint64 timestamp;
    }

    function getTransfersInvolvingUser(address user) external view returns (Transfer[] memory);
}

interface IAssetPurchase {
    struct Purchase {
        address buyer;
        uint64 assetId;
        uint256 price;
        uint64 timestamp;
    }

    function getUserPurchases(address user) external view returns (Purchase[] memory);
}

interface ISmartVault {
    struct Redemption {
        uint256 id;
        address user;
        uint128 amount;
        uint256 requestedAt;
        bool fulfilled;
    }

    function getUserVaultInfo(address user) external view returns (
        uint128 amount,
        uint32 committedQuarters,
        uint32 unlockQuarter,
        uint256[] memory redemptionIds,
        Redemption[] memory redemptions
    );

    function getUserDepositStatus(address user) external view returns (
        uint256 depositAmount,
        uint256 depositStartTime,
        uint256 elapsedDays,
        uint256 remainingDays,
        bool isPending,
        bool isClosed
    );
}

interface IStableSwapGateway {
    enum SwapStatus { Pending, Completed, Refunded }

    struct SwapRecord {
        address fromToken;
        address toToken;
        uint256 fromAmount;
        uint256 toAmount;
        uint256 fee;
        SwapStatus status;
        uint64 timestamp;
    }

    function getUserSwaps(address user) external view returns (SwapRecord[] memory);
    function getSwapsByStatus(address user, SwapStatus status) external view returns (SwapRecord[] memory);
}

contract UserQueryHub {
    ITransferTracker public transferTracker;
    IAssetPurchase public assetPurchase;
    ISmartVault public smartVault;
    IStableSwapGateway public stableSwapGateway;

    constructor(
        address _transferTracker,
        address _assetPurchase,
        address _smartVault,
        address _stableSwapGateway
    ) {
        transferTracker = ITransferTracker(_transferTracker);
        assetPurchase = IAssetPurchase(_assetPurchase);
        smartVault = ISmartVault(_smartVault);
        stableSwapGateway = IStableSwapGateway(_stableSwapGateway);
    }

    function getUserActivity(address user) external view returns (
        ITransferTracker.Transfer[] memory transfers,
        IAssetPurchase.Purchase[] memory purchases,
        ISmartVault.Redemption[] memory redemptions,
        IStableSwapGateway.SwapRecord[] memory swaps
    ) {
        transfers = transferTracker.getTransfersInvolvingUser(user);
        purchases = assetPurchase.getUserPurchases(user);

        (, , , , redemptions) = smartVault.getUserVaultInfo(user);
        swaps = stableSwapGateway.getUserSwaps(user);
    }

    function getUserVaultStatus(address user) external view returns (
        uint256 depositAmount,
        uint256 depositStartTime,
        uint256 elapsedDays,
        uint256 remainingDays,
        bool isPending,
        bool isClosed
    ) {
        return smartVault.getUserDepositStatus(user);
    }
}

contracts/WGBD.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract WGBD {
    string public name = "Wrapped GBD";
    string public symbol = "WGBD";
    uint8  public decimals = 18;

    event  Approval(address indexed src, address indexed guy, uint wad);
    event  Transfer(address indexed src, address indexed dst, uint wad);
    event  Deposit(address indexed dst, uint wad);
    event  Withdrawal(address indexed src, uint wad);

    mapping(address => uint)                       public  balanceOf;
    mapping(address => mapping(address => uint))  public  allowance;

    receive() external payable {
        deposit();
    }

    function deposit() public payable {
        balanceOf[msg.sender] += msg.value;
        emit Deposit(msg.sender, msg.value);
    }

    function withdraw(uint wad) public {
        require(balanceOf[msg.sender] >= wad, "insufficient balance");
        balanceOf[msg.sender] -= wad;
        payable(msg.sender).transfer(wad);
        emit Withdrawal(msg.sender, wad);
    }

    function approve(address guy, uint wad) public returns (bool) {
        allowance[msg.sender][guy] = wad;
        emit Approval(msg.sender, guy, wad);
        return true;
    }

    function transfer(address dst, uint wad) public returns (bool) {
        return transferFrom(msg.sender, dst, wad);
    }

    function transferFrom(address src, address dst, uint wad)
        public
        returns (bool)
    {
        require(balanceOf[src] >= wad, "insufficient balance");

        if (src != msg.sender && allowance[src][msg.sender] != type(uint).max) {
            require(allowance[src][msg.sender] >= wad, "insufficient allowance");
            allowance[src][msg.sender] -= wad;
        }

        balanceOf[src] -= wad;
        balanceOf[dst] += wad;

        emit Transfer(src, dst, wad);
        return true;
    }
}
